#!/usr/bin/env python3
"""Test script to verify Gemma3 vision functionality with Ollama."""

import requests
import json
import base64
from pathlib import Path

def create_test_image_base64():
    png = (
        ""
    )
    return png

def test_gemma3_vision():
    """Test if Gemma3 can process images."""
    api_endpoint = "http://localhost:11434/api/generate"
    
    # Create test payload
    test_image = create_test_image_base64()
    
    payload = {
        "model": "gemma3:12b",
        "prompt": "Which creature in this image has a cone head?",
        "images": [test_image],
        "stream": False,
        "options": {
            "temperature": 0.7
        }
    }
    
    print("Testing Gemma3 vision with Ollama...")
    print(f"API Endpoint: {api_endpoint}")
    print(f"Image data length: {len(test_image)}")
    print(f"Payload keys: {list(payload.keys())}")
    
    try:
        response = requests.post(
            api_endpoint,
            json=payload,
            headers={"Content-Type": "application/json"},
            timeout=60
        )
        
        print(f"Response status: {response.status_code}")
        
        if response.status_code != 200:
            print(f"Error response: {response.text}")
            return False
            
        result = response.json()
        print(f"Response: {json.dumps(result, indent=2)}")
        
        if "response" in result:
            print(f"\nGenerated text: {result['response']}")
            return True
        else:
            print("No 'response' field in result")
            return False
            
    except Exception as e:
        print(f"Error during request: {e}")
        return False

if __name__ == "__main__":
    success = test_gemma3_vision()
    print(f"\nTest {'PASSED' if success else 'FAILED'}")
